mapper.select:

select

  distinct


from pay_order_yinjia_api




  order by ${orderByClause}


  limit ${(pageNo-1) * pageSize}, ${pageSize}

SELECT a.*,m.merch_name, m.username,m.companyname,t.cat FROM pay_order_yinjia_api a LEFT JOIN pay_merchants m ON a.mtsid = m.id LEFT JOIN pay_merchants_type t ON a.paytype = t.catid
order by ${orderByClause}
limit ${(pageNo-1) * pageSize}, ${pageSize}

SELECT a.orderid,a.foreign_orderid,a.sign_orderid,a.mtsid, a.paytype,a.order_pay_price,a.pay_detail, a.pay_status,a.refund_status,a.user_operate_status FROM pay_order_yinjia_api a WHERE a.orderid NOT IN ( SELECT orderid FROM pay_orders_money_detail ) and a.paytype = 7
order by ${orderByClause}
limit ${(pageNo-1) * pageSize}, ${pageSize}
