  order by ${orderByClause}


  limit ${(pageNo-1) * pageSize}, ${pageSize}

select id, stock_order_id, stock_order_no, product_id, product_name, customer_id, customer_name, operator_id, operator_name, init_money, init_amount, daily_in_money, daily_in_amount, daily_out_money, daily_out_amount, final_money, final_amount, ADDTIME from pay_shop_stock_log
order by ${orderByClause}
Limit 1
